mapper.select:
SELECT *
FROM t_word
WHERE 1=1

	AND noun = #{noun}


	AND pronoun = #{pronoun}


	AND verb = #{verb}


	AND adjective = #{adjective}


	AND er = #{er}


	AND est = #{est}


	AND adverb = #{adverb}


	AND preposition = #{preposition}


	AND conjunction = #{conjunction}


	AND explanation = #{explanation}

		SELECT wt.*,t.tag
		FROM t_tag t, t_word_tag wt
		WHERE wt.tag_id = t.id
		AND wt.word_id IN (

                #{wordId} 

		);

		SELECT *
		FROM t_phrase
		WHERE word_id IN (

                #{wordId} 

		);

		SELECT *
		FROM t_sentence
		WHERE word_id IN (

                #{wordId} 

		);
